use strict;
use warnings;

my $dna='';
my @bases=split(//,$dna);
my $l=@bases;
my %hash=();

for(my $i=0;$i<$l;$i++)
{
    my $nucl=$bases[$i];
    $hash{$nucl}++;
    
}

if (exists $hash{Z})
{
    print "Z ist da!/n";
}

print "A ", $hash{A}, "\n";

foreach my $schluessel (sort keys %hash)
{
    if ($schluessel eq "A")
    {
	print "$schluessel ";
	print $hash{$schluessel},"\n";
    }
}

